sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);



	function TestCase_DeployerTargetAPI_GetStatus(oBasicAuth, vExpected)
	{
		function TestCase_DeployerTargetAPI_GetStatus_CheckResult(vInput,vExpected,vResult)
		{
			var vResultParsed = x_snc_devtools.ParseJson(vResult);
			test.Log('Result Parsed: '+x_snc_devtools.RenderValue(vResultParsed));
			if (vExpected == 'SUCCESS')
			{
				if (x_snc_devtools.GetValue(vResultParsed,'result','message') == 'SUCCESS')
				{
					return true;
				}
				return false;
			}
			if (x_snc_devtools.GetValue(vResultParsed,'error','message') == vExpected)
			{
				return true;
			}
			return false;
		}
		
		var strUrl = '';
		strUrl += x_snc_devtools.GetInstanceUrl();
		strUrl += '/api/x_snc_deployer/target/get_status';
		strUrl += '?';
		
		test.TestCase_HttpRequest(
			'Deployer Target API / Get Status',
			{
				url: strUrl,
				basicauth: oBasicAuth
			},
			vExpected,
			TestCase_DeployerTargetAPI_GetStatus_CheckResult);

	}

	var strPassword = x_snc_devtools.GetRandomNumericToken(30);
	
	var testdata = new x_snc_devtools.TestDataAPI('_ATF_DEPLOYER_');

	var strUserItil = testdata.CreateUser('ITIL',['itil'],false,'',strPassword);
	var grUserItil = x_snc_devtools.GetUserRecord(strUserItil);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserItil.user_name),
		password: strPassword
	};
	TestCase_DeployerTargetAPI_GetStatus(oBasicAuth,'User Not Authorized');

	
	var strUserApi = testdata.CreateUser(
		'DEPLOYER_TARGET_API',
		['x_snc_deployer.api_target'],
		true,'',strPassword);
	var grUserApi = x_snc_devtools.GetUserRecord(strUserApi);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserApi.user_name),
		password: strPassword
	};
	TestCase_DeployerTargetAPI_GetStatus(oBasicAuth,'SUCCESS');
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
