sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetRepositoryApiUrl(strRepoUrl, vExpected)
	{
		function TestCase_GetRepositoryApiUrl_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function GetRepositoryApiUrl()',
			GetRepositoryApiUrl,
			{
				'strRepoUrl': strRepoUrl
			},
			vExpected,
			TestCase_GetRepositoryApiUrl_CheckResult);
	}

	TestCase_GetRepositoryApiUrl(null,false);


	TestCase_GetRepositoryApiUrl('https://guthib.com/example/example.git',false);

	var grRepoCred = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCred.initialize();
	grRepoCred.active = true;
	grRepoCred.type = 'github';
	grRepoCred.url = 'https://guthib.com/example/';
	grRepoCred.insert();

	var githubapi = new x_snc_devtools.GitHubAPI();
	TestCase_GetRepositoryApiUrl('https://guthib.com/example/example.git',githubapi.m_strApiUrl);


	var grRepoCred = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCred.initialize();
	grRepoCred.active = true;
	grRepoCred.type = 'github';
	grRepoCred.github_on_premise = true;
	grRepoCred.api_url = 'https://api.github.acme.com/';
	grRepoCred.url = 'https://github.acme.com/example/';
	grRepoCred.insert();
	TestCase_GetRepositoryApiUrl('https://github.acme.com/example/example.git','https://api.github.acme.com/');	

	var grRepoCred = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCred.initialize();
	grRepoCred.active = true;
	grRepoCred.type = 'gitlab';
	grRepoCred.url = 'https://gitlab.acme.com/example/';
	grRepoCred.api_url = 'https://gitlab.acme.com/api/';
	grRepoCred.insert();
	var githubapi = new x_snc_devtools.GitHubAPI();
	TestCase_GetRepositoryApiUrl('https://gitlab.acme.com/example/example.git','https://gitlab.acme.com/api/');	
	

	var grRepoCred = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCred.initialize();
	grRepoCred.active = true;
	grRepoCred.type = 'azuredevops';
	grRepoCred.url = 'https://dev.azure.com/org/prj/';
	grRepoCred.insert();
	TestCase_GetRepositoryApiUrl('https://dev.azure.com/org/prj/_git/repo','https://dev.azure.com/');


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
