sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		null,
		null,
		'User Not Authenticated');
	
	
	var testdata = new x_snc_devtools.TestDataAPI('Deployer');
	var strPassword = x_snc_devtools.GetRandomNumericToken(30);
	
	var strUserNoRolesSysId = testdata.CreateUser('No Roles',[],false,'',strPassword);
	var grUserNoRoles = x_snc_devtools.GetUserRecord(strUserNoRolesSysId);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserNoRoles.user_name),
		password: strPassword
	};
	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		null,
		'User Not Authorized');
	
	var strUserTargetApiSysId = testdata.CreateUser(
		'DEPLOYER_TARGET_API',
		['x_snc_deployer.api_target'],
		false,'',strPassword);
	var grUserApi = x_snc_devtools.GetUserRecord(strUserTargetApiSysId);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserApi.user_name),
		password: strPassword
	};
	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		null,
		'User Not Authorized');	

	
	var strUserApiSysId = testdata.CreateUser(
		'DEPLOYER_CONTROLLER_API',
		['x_snc_deployer.api_controller'],
		false,'',strPassword);
	var grUserApi = x_snc_devtools.GetUserRecord(strUserApiSysId);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserApi.user_name),
		password: strPassword
	};
	
	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		null,
		'INSTANCE_NOT_SPECIFIED');	


	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		{
			instance: 'TEST-ATF'
		},
		'INSTANCE_NOT_FOUND');	

	var grInstance = new GlideRecord('x_snc_deployer_instance');
	grInstance.initialize();
	grInstance.name = 'TEST-ATF';
	var strInstanceSysId = grInstance.insert();	

	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		{
			instance: 'TEST-ATF'
		},
		'REPO_URL_NOT_SPECIFIED');	

	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		{
			instance: 'TEST-ATF',
			repo_url: 'bogus'
		},
		'REPO_URL_INVALID');	

	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		{
			instance: 'TEST-ATF',
			repo_url: 'https://www.example.com/test-repo/'
		},
		'BRANCH_NOT_SPECIFIED');			

	test.TestCase_RestApiOperation(
		'Deployer Controller API',
		'Start Deployment',
		oBasicAuth,
		{
			instance: 'TEST-ATF',
			repo_url: 'https://www.example.com/test-repo/',
			branch: 'dev'
		},
		'SUCCESS');


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
