sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_IdentifyDataDeletion(aAppsCurrent, aAppsToBeInstalled, vExpected)
	{
		function TestCase_IdentifyDataDeletion_CheckResult(vInput,vExpected,vResult)
		{
			return x_snc_devtools.IsEqual(vExpected,vResult);
		}

		test.TestCase(
			'Function IdentifyDataDeletion()',
			IdentifyDataDeletion,
			{
				'aAppsCurrent': aAppsCurrent,
				'aAppsToBeInstalled': aAppsToBeInstalled,
			},
			vExpected,
			TestCase_IdentifyDataDeletion_CheckResult);
	}

	TestCase_IdentifyDataDeletion(null,null,
		{
			datadeletion: false,
			_sys_db_object: [],
			_sys_dictionary: []
		});

	TestCase_IdentifyDataDeletion(
		[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha'
			}
		],
		null,
		{
			datadeletion: false,
			_sys_db_object: [],
			_sys_dictionary: []
		});		

	TestCase_IdentifyDataDeletion(
		[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha',
				_sys_db_object:
				[
					{
						name: 'Table 1'
					}
				]
			}
		],
				[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha'
			}
		],
		false);					

	TestCase_IdentifyDataDeletion(
		[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha',
				_sys_db_object:
				[
					{
						name: 'Table 1'
					}
				]
			}
		],
				[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha',
				_sys_db_object:
				[
				]
			}
		],
		{
			datadeletion: true,
			_sys_db_object: [
				{
					name: 'Table 1'
				}
			],
			_sys_dictionary: []
		});			

	TestCase_IdentifyDataDeletion(
		[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha',
				_sys_db_object:
				[
					{
						name: 'Table 1'
					}
				],
				_sys_dictionary:
				[
					{
						name: 'incident',
						element: 'test_column_1',
						label: 'Test 1'
					}
				]
			}
		],
				[
			{
				sys_id: x_snc_devtools.GetPseudoSysId(),
				name: 'App Alpha',
				_sys_db_object:
				[
				]
			}
		],
		{
			datadeletion: true,
			_sys_db_object: [
				{
					name: 'Table 1'
				}
			],
			_sys_dictionary:
			[
				{
					name: 'incident',
					element: 'test_column_1',
					label: 'Test 1'
				}
			]
		});	


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
