sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetRepositoryCredentialRecord(vRepo, vExpected)
	{
		function TestCase_GetRepositoryCredentialRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			if (x_snc_devtools.IsValidRecord(vResult) == false)
			{
				test.Log('IsValidRecord returned false');
				return false;
			}
			test.Log(vResult.sys_id);
			return x_snc_devtools.IsEqual(vExpected,x_snc_devtools.GetStringValue(vResult.sys_id));
		}

		test.TestCase(
			'Function GetRepositoryCredentialRecord()',
			GetRepositoryCredentialRecord,
			{
				'vRepo': vRepo
			},
			vExpected,
			TestCase_GetRepositoryCredentialRecord_CheckResult);
	}

	TestCase_GetRepositoryCredentialRecord(null,false);
	TestCase_GetRepositoryCredentialRecord('bogus',false);

	
	var grCredential = new GlideRecord('basic_auth_credentials');
	grCredential.initialize();
	grCredential.type = 'basic_auth';
	grCredential.name = 'GitHub Test1';
	var strCredentialGitHubTest1SysId = x_snc_devtools.GlideRecordInsert(grCredential);
	
	var grRepoCredential = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCredential.initialize();
	grRepoCredential.url = 'https://github.example.com/';
	grRepoCredential.type = 'github';
	grRepoCredential.active = true;
	grRepoCredential.credential = strCredentialGitHubTest1SysId;
	var strRepoCredentialGitHubTest1SysId = x_snc_devtools.GlideRecordInsert(grRepoCredential);

	TestCase_GetRepositoryCredentialRecord(grRepoCredential,strRepoCredentialGitHubTest1SysId);
	TestCase_GetRepositoryCredentialRecord('https://github.example.com/tester/examplerepo.git',strRepoCredentialGitHubTest1SysId);
	

	var grCredential = new GlideRecord('basic_auth_credentials');
	grCredential.initialize();
	grCredential.type = 'basic_auth';
	grCredential.name = 'GitLab Test1';
	var strCredentialGitLabTest1SysId = x_snc_devtools.GlideRecordInsert(grCredential);
	
	var grRepoCredential = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCredential.initialize();
	grRepoCredential.url = 'https://gitlab.example.com/somegroup/';
	grRepoCredential.type = 'gitlab';
	grRepoCredential.active = true;
	grRepoCredential.api_url = 'https://gitlab.example.com/api/v4/';
	grRepoCredential.credential = strCredentialGitLabTest1SysId;
	var strRepoCredentialGitLabTest1SysId = x_snc_devtools.GlideRecordInsert(grRepoCredential);

	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/somegroup/tester/examplerepo.git',strRepoCredentialGitLabTest1SysId);
	

	var grCredential = new GlideRecord('basic_auth_credentials');
	grCredential.initialize();
	grCredential.type = 'basic_auth';
	grCredential.name = 'GitLab Test2';
	var strCredentialGitLabTest2SysId = x_snc_devtools.GlideRecordInsert(grCredential);	
	
	var grRepoCredential = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCredential.initialize();
	grRepoCredential.url = 'https://gitlab.example.com/othergroup/';
	grRepoCredential.type = 'gitlab';
	grRepoCredential.api_url = 'https://gitlab.example.com/api/v4/';
	grRepoCredential.active = true;
	grRepoCredential.credential = strCredentialGitLabTest2SysId;
	var strRepoCredentialGitLabTest2SysId = x_snc_devtools.GlideRecordInsert(grRepoCredential);	

	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/othergroup/tester2/examplerepo2.git',strRepoCredentialGitLabTest2SysId);

	
	
	var grCredential = new GlideRecord('basic_auth_credentials');
	grCredential.initialize();
	grCredential.type = 'basic_auth';
	grCredential.name = 'GitLab Test3';
	var strCredentialGitLabTest3SysId = x_snc_devtools.GlideRecordInsert(grCredential);	
	
	var grRepoCredential = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCredential.initialize();
	grRepoCredential.url = 'https://gitlab.example.com/';
	grRepoCredential.type = 'gitlab';
	grRepoCredential.api_url = 'https://gitlab.example.com/api/v4/';
	grRepoCredential.active = true;
	grRepoCredential.credential = strCredentialGitLabTest3SysId;
	var strRepoCredentialGitLabTest3SysId = x_snc_devtools.GlideRecordInsert(grRepoCredential);	

	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/somegroup/tester/examplerepo.git',strRepoCredentialGitLabTest1SysId);
	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/othergroup/tester2/examplerepo2.git',strRepoCredentialGitLabTest2SysId);
	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/yetanothergroup/tester3/examplerepo3.git',strRepoCredentialGitLabTest3SysId);
	
	x_snc_devtools.RecordSetValue('x_snc_deployer_repository_credentials',strRepoCredentialGitLabTest3SysId,'active',false);
	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/somegroup/tester/examplerepo.git',strRepoCredentialGitLabTest1SysId);
	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/othergroup/tester2/examplerepo2.git',strRepoCredentialGitLabTest2SysId);
	TestCase_GetRepositoryCredentialRecord('https://gitlab.example.com/yetanothergroup/tester3/examplerepo3.git',false);	
	
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
