sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);



	function TestCase_DeployerTargetAPI_GetCredential(oBasicAuth, strRepoUrl, vExpected)
	{
		function TestCase_DeployerTargetAPI_GetCredential_CheckResult(vInput,vExpected,vResult)
		{
			var vResultParsed = x_snc_devtools.ParseJson(vResult);
			test.Log('Result Parsed: '+x_snc_devtools.RenderValue(vResultParsed));
			if (x_snc_devtools.IsValidSysId(vExpected))
			{
				if (x_snc_devtools.GetValue(vResultParsed,'result','message') != 'SUCCESS')
				{
					return false;
				}
				if (x_snc_devtools.GetValue(vResultParsed,'result','credential_sys_id') != vExpected)
				{
					return false;
				}
				return true;
			}
			if (x_snc_devtools.GetValue(vResultParsed,'error','message') == vExpected)
			{
				return true;
			}
			return false;
		}
		
		var strUrl = '';
		strUrl += x_snc_devtools.GetInstanceUrl();
		strUrl += '/api/x_snc_deployer/target/get_credential';
		strUrl += '?';
		if (strRepoUrl != '')
		{
			strUrl += 'repo_url=' + encodeURIComponent(strRepoUrl);
		}
		
		test.TestCase_HttpRequest(
			'Deployer Target API / Get Credential',
			{
				url: strUrl,
				basicauth: oBasicAuth
			},
			vExpected,
			TestCase_DeployerTargetAPI_GetCredential_CheckResult);

	}


	var strRepoUrl = 'https://guthib.com/test/test';


	var strPassword = x_snc_devtools.GetRandomNumericToken(30);
	
	var testdata = new x_snc_devtools.TestDataAPI('_ATF_DEPLOYER_');

	var strUserItil = testdata.CreateUser('ITIL',['itil'],false,'',strPassword);
	var grUserItil = x_snc_devtools.GetUserRecord(strUserItil);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserItil.user_name),
		password: strPassword
	};
	TestCase_DeployerTargetAPI_GetCredential(oBasicAuth,'','User Not Authorized');


	var strUserApi = testdata.CreateUser('API',['x_snc_deployer.api'],true,'',strPassword);
	var grUserApi = x_snc_devtools.GetUserRecord(strUserApi);
	var oBasicAuth = {
		username: x_snc_devtools.GetStringValue(grUserApi.user_name),
		password: strPassword
	};
	TestCase_DeployerTargetAPI_GetCredential(oBasicAuth,'','REPO_URL_INVALID');
	
	TestCase_DeployerTargetAPI_GetCredential(oBasicAuth,strRepoUrl,'NO_REPOSITORY_CREDENTIAL_AVAILABLE');

	var grCredential = new GlideRecord('discovery_credentials');
	grCredential.initialize();
	grCredential.name = 'TEST API KEY';
	var strCredentialSysId = x_snc_devtools.GlideRecordInsert(grCredential);

	test.Log(x_snc_devtools.GetRecord('discovery_credentials',strCredentialSysId));

	var grRepoCred = new GlideRecord('x_snc_deployer_repository_credentials');
	grRepoCred.initialize();
	grRepoCred.active = true;
	grRepoCred.type = 'github';
	grRepoCred.credential = strCredentialSysId;
	grRepoCred.url = 'https://guthib.com/';
	var strRepoCredSysId = x_snc_devtools.GlideRecordInsert(grRepoCred);

	test.Log(x_snc_devtools.GetRecord('x_snc_deployer_repository_credentials',strRepoCredSysId));

	TestCase_DeployerTargetAPI_GetCredential(oBasicAuth,strRepoUrl,strCredentialSysId);



	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
