sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_GetLatestRepoDependencyList(strRepoUrl, strVersion, vExpected)
	{
		function TestCase_GetLatestRepoDependencyList_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			return x_snc_devtools.IsArray(vResult);
		}

		test.TestCase(
			'Function GetLatestRepoDependencyList()',
			GetLatestRepoDependencyList,
			{
				'Repo Url': strRepoUrl,
				'Version': strVersion,
			},
			vExpected,
			TestCase_GetLatestRepoDependencyList_CheckResult);
	}

	TestCase_GetLatestRepoDependencyList(null,null,false);
	
	var strRepoUrl = 'https://github.com/example/example.git';
	var strVersion = '1.0.0';
	TestCase_GetLatestRepoDependencyList(strRepoUrl,strVersion,false);
	
	var grRepoAppStatus = new GlideRecord('x_snc_deployer_repository_application_status');
	grRepoAppStatus.initialize();
	grRepoAppStatus.repo_url = strRepoUrl;
	grRepoAppStatus.version = strVersion;
	grRepoAppStatus.status = 'error';
	grRepoAppStatus.sys_created_on = x_snc_devtools.RenderDateTime(x_snc_devtools.GetTimeNow());
	x_snc_devtools.GlideRecordInsert(grRepoAppStatus,true);
	TestCase_GetLatestRepoDependencyList(strRepoUrl,strVersion,false);
	
	// To ensure both records get different time stamps
	x_snc_devtools.Sleep(2);	
	
	var grRepoAppStatus = new GlideRecord('x_snc_deployer_repository_application_status');
	grRepoAppStatus.initialize();
	grRepoAppStatus.repo_url = strRepoUrl;
	grRepoAppStatus.version = strVersion;
	grRepoAppStatus.dependency_tree_json = '{}';
	grRepoAppStatus.status = 'ok';
	grRepoAppStatus.sys_created_on = x_snc_devtools.RenderDateTime(x_snc_devtools.GetTimeNow());
	var strRepoAppStatusSysId = x_snc_devtools.GlideRecordInsert(grRepoAppStatus,true);

	// To avoid potential DB race conditions
	x_snc_devtools.Sleep(2);

	//test.Log(x_snc_devtools.GetRecord('x_snc_deployer_repository_application_status',strRepoAppStatusSysId));

	TestCase_GetLatestRepoDependencyList(strRepoUrl,strVersion,true);	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
