sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate)
{
	if (isLoading || newValue === '')
	{
		return;
	}

	if (newValue == 'true')
	{
		DoModalMessage(
			'Warning',
			'Be careful with activating "Enforce Downgrade"!\n'
			+'This may lead to installing OLDER versions of applications than are already installed on the target instance.',
			function(){});
	}
	else if (newValue == 'false')
	{
		g_form.setValue('enforce_downgrade','false');
	}

}
