sys_script_include.script:
function TargetInstanceSourceControlApplyChanges(grInstance,strAppId,strBranch)
{
	var GetValue = x_snc_devtools.GetValue;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsValidSysId = x_snc_devtools.IsValidSysId;


	strAppId = GetStringValue(strAppId);

	var strAppParam = '';
	if (IsValidSysId(strAppId) == true)
	{
		strAppParam = 'app_sys_id='+strAppId;
	}
	else
	{
		strAppParam = 'app_scope='+strAppId;
	}

	var strBranchParam = 'branch_name=' + encodeURIComponent(strBranch);

	var strQuery = strAppParam + '&' + strBranchParam;

	var result = TargetInstanceRequest(grInstance,'sn_cicd/sc/apply_changes?'+strQuery,'{}');


	var strProgressId = GetValue(result,'result','links','progress','id');

	if (IsValidSysId(strProgressId) == false)
	{
		return false;
	}

	return strProgressId;
}
