sys_script_include.script:
function IsDeploymentTaskExecutionFinished(vDeployment)
{
	var GetRecord = x_snc_devtools.GetRecord;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;

	var grDeployment = GetRecord('x_snc_deployer_deployment',vDeployment);
	if (IsValidRecord(grDeployment) == false)
	{
		return false;
	}

	if (grDeployment.state == GetDeploymentState('complete'))
	{
		return true;
	}
	if (grDeployment.state == GetDeploymentState('cancelled'))
	{
		return true;
	}
	if (grDeployment.state == GetDeploymentState('failed'))
	{
		return true;
	}


	if (grDeployment.state != GetDeploymentState('inprogress'))
	{
		return false;
	}

	var aStates = [
			GetDeploymentTaskState('ready'),
			GetDeploymentTaskState('pending'),
			GetDeploymentTaskState('inprogress')
		];

	var grTask = new GlideRecord('x_snc_deployer_deployment_task');
	grTask.addQuery('deployment','=',grDeployment.sys_id);
	grTask.orderBy('order');
	grTask.addQuery('state','IN',GetStringValue(aStates));
	grTask.setLimit(1);
	grTask.query();
	if (grTask.next())
	{
		return false;
	}
	return true;
}
