sys_script_include.script:
function IsDeploymentReadyToComplete(grDeployment)
{
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	if (IsValidRecord(grDeployment) == false)
	{
		return false;
	}	
	if (grDeployment.state != GetDeploymentState('inprogress'))
	{
		return false;
	}
	var grTasks = new GlideRecord('x_snc_deployer_deployment_task');
	grTasks.addQuery('deployment','=',grDeployment.sys_id);
	grTasks.addQuery('state','!=',GetDeploymentTaskState('complete'));
	grTasks.addQuery('state','!=',GetDeploymentTaskState('skipped'));
	grTasks.query();
	if (grTasks.getRowCount() != 0)
	{
		return false;
	}
	return true;
}
