sys_script_include.script:
function GetInstanceRecord(vInstance)
{
	var GetRecord = x_snc_devtools.GetRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsValidRecord = x_snc_devtools.IsValidRecord;

	var grInstance = GetRecord('x_snc_deployer_instance',vInstance);
	if (IsValidRecord(grInstance) == true)
	{
		return grInstance;
	}
	grInstance = new GlideRecord('x_snc_deployer_instance');
	grInstance.addQuery('name','=',GetStringValue(vInstance));
	grInstance.query();
	if (grInstance.getRowCount() != 1)
	{
		return false;
	}
	grInstance.next();
	return grInstance;
}
