sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var GetBoolValue = x_snc_devtools.GetBoolValue;
	var IsValidSysId = x_snc_devtools.IsValidSysId
	var GetStringValue = x_snc_devtools.GetStringValue;
	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var IsValidUrl = x_snc_devtools.IsValidUrl;
	var IsUserHasRoleExactly = x_snc_devtools.IsUserHasRoleExactly;

	function GetParam(strParam)
	{
		var queryParams = request.queryParams;
		return x_snc_devtools.GetValue(queryParams,strParam);
	}

	function Error(strMessage,strDetails)
	{
		var myError = new sn_ws_err.ServiceError();
		myError.setStatus(200);
		myError.setMessage(strMessage);
		myError.setDetail(strDetails);
		response.setError(myError);	
	}

	if (IsUserHasRoleExactly('x_snc_deployer.api_controller') == false)
	{
		Error('User Not Authorized','');
		return;
	}

	if (IsActive() == false)
	{
		Error('INACTIVE','The Deployer app is inactive.');
		return;
	}

	var strInstance = GetStringValue(GetParam('instance'));
	var strRepoUrl = GetStringValue(GetParam('repo_url'));
	var strBranch = GetStringValue(GetParam('branch'));

	if (strInstance == '')
	{
		Error('INSTANCE_NOT_SPECIFIED','No instance has been specified!');
		return;
	}


	var grInstance = GetInstanceRecord(strInstance);
	if (IsValidRecord(grInstance) == false)
	{
		Error('INSTANCE_NOT_FOUND','The specified Instance (configuration) cannot be found!');
		return;
	}

	if (strRepoUrl == '')
	{
		Error('REPO_URL_NOT_SPECIFIED','No repository URL is specified!');
		return;
	}


	if (IsValidUrl(strRepoUrl) == false)
	{
		Error('REPO_URL_INVALID','The specified Repository URL is invalid!');
		return;
	}

	if (strBranch == '')
	{
		Error('BRANCH_NOT_SPECIFIED','No branch is specified!');
		return;
	}


	var strProgressName = 'Performing headless deployment on instance: '+grInstance.name;

	var params = {};
	params['strInstance'] = strInstance;
	params['strRepoUrl'] = strRepoUrl;
	params['strBranch'] = strBranch;
	var strWorkerId = x_snc_devtools.StartWorker(
		strProgressName,
		'x_snc_deployer.DeployerWorkerAPI',
		'HeadlessDeployment',
		params);	

	if (IsValidSysId(strWorkerId) == false)
	{
		Error('ASYNC_START_FAILED','Failed to start headless deployment asynchronously!');
		return;
	}

	var body = {};
	body.message = 'SUCCESS';
	body.detail = 'Started headless deployment.';
	body.progress_id = strWorkerId;
	response.setBody(body);

	return;

})(request, response);
